import styled from "styled-components"

export const Container = styled.div `
    background-color: grey;
    width: 250px;
    display: flex;
    text-transform: column;
    text-align: center;
`

export const H1 = styled.h1 `
    color: #ffffff;
    font-size: 30px;
`

export const P = styled.p `
    color: #ffffff;
    font-size: 18px;
`
